// Hook imports
import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

// Style imports 
import './NavBar.scss';

// Image imports
import banner from '../images/banner.jpg';



export default function Home(props) {
  const navigate = useNavigate();
  const [active, setActive] = useState("")

  const clickNavItem = (item) => {
    if (active !== "") {
      navigate('/'+item)
      setActive(item)
      console.log(item)
    }

  }

  useEffect(() => {
    window.scrollTo(0, 0)
  }, [])

  return (
    <div className={props.size}>
    <div className="NavBar">
      <span 
        className="NavItem"
        onClick={clickNavItem('sculpture')}
      >
        SCULPTURE
      </span>
      <span 
        className="NavItem"
        onClick={clickNavItem('process')}
      >
        PROCESS
      </span>
      <span 
        className="NavItem"
        onClick={clickNavItem('contact')}
      >
        CONTACT
      </span>
    </div>
    </div>
  );
}
